	function curl_redirect_exec($channel, $redirects, $curlopt_header = false) {
		curl_setopt($channel, CURLOPT_HEADER, true);
		curl_setopt($channel, CURLOPT_RETURNTRANSFER, true);
		$data = curl_exec($channel);
		$http_code = curl_getinfo($channel, CURLINFO_HTTP_CODE);
		if ($http_code == 301 || $http_code == 302) {
			// "\r\n\r\n" = format windows klo linux cukup "\n\n". Silahkan di tes aja mana yang cocok itu yang digunakan oke ^_^
			list($header) = explode("\r\n\r\n", $data, 2);
			$matches = array();
			preg_match('/(Location:|URI:)(.*?)\n/', $header, $matches);
			$url = trim(array_pop($matches));
			$url_parsed = parse_url($url);
			if (isset($url_parsed)) {
				curl_setopt($channel, CURLOPT_URL, $url);
				$redirects++;
				return $this->curl_redirect_exec($channel, $redirects);
			}
		}
		if ($curlopt_header)
			return $data;
		else {
			$explode = explode("\r\n\r\n", $data, 2);
			if(count($explode) > 1) {
				list(,$body) = $explode;
			} else list($body) = $explode;
			return $body;
		}
	}
